function soundInit(){console.log("in init");try{ctx=new(window.AudioContext||window.webkitAudioContext),loadFile()}catch(t){alert("you need webaudio support"+t)}}function loadFile(){var t=new XMLHttpRequest;t.open("GET","sounds/day.mp3",!0),t.responseType="arraybuffer",t.onload=function(){ctx.decodeAudioData(t.response,function(t){buf=t})},t.send()}function play(){var t=ctx.createBufferSource();t.buffer=buf,fft=ctx.createAnalyser(),fft.smoothingTimeConstant=.3,fft.fftSize=samples,t.connect(fft),fft.connect(ctx.destination),t.start(0),setup=!0}function soundUpdate(){var t=new Uint8Array(samples);fft.getByteFrequencyData(t),console.log(t)}var ctx,buf,fft,samples=512,setup=!1;window.addEventListener("load",soundInit,!1);